"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

README.md.content:
# Discord Bot License Verification API

A secure API service for managing and verifying Discord bot licenses using MongoDB with comprehensive license tracking.

## Features

- âœ… Secure API key authentication
- âœ… MongoDB integration for license storage
- âœ… Rate limiting (100 requests per minute)
- âœ… License expiration checking with lifetime support
- âœ… **Automatic guild tracking** - tracks which Discord servers are using each license
- âœ… **Guild limit enforcement** - prevents licenses from being used on more servers than allowed
- âœ… Product and owner management
- âœ… Active/inactive status management
- âœ… Full CRUD operations for licenses
- âœ… Usage analytics and verification logging
- âœ… Metadata tracking (notes, reseller info)
- âœ… Verification checksum support

## License Structure

The comprehensive license structure includes:

```typescript
{
  _id: string,                          // MongoDB document ID (optional)
  licenseKey: string,                   // Unique license identifier
  product: {
    name: string,                       // Product name (e.g., "Crim Tickets")
    id: string                          // Product ID (e.g., "prod_001")
  },
  owner: {
    userId: string,                     // User/client ID
    email: string                       // Owner email
  },
  validity: {
    issuedAt: Date,                     // License issue date
    expiresAt: Date | "never",          // Expiration date or "never"
    durationDays: number | "lifetime"   // Duration or "lifetime"
  },
  status: "active" | "inactive",        // License status
  activation: {
    maxGuilds: number,                  // Maximum allowed Discord servers
    activeGuilds: string[]              // Array of active Discord server IDs
  },
  metadata: {
    notes: string,                      // Additional notes
    reseller: string                    // Reseller information
  },
  verification: {
    lastVerified: Date,                 // Last verification timestamp
    checksum: string                    // Verification checksum
  }
}
```

## API Endpoints

### License Verification

#### POST `/api/verify-license`

Verify if a license key is valid and automatically track the Discord server.

**How it works:**
- When your bot starts in a guild, call this endpoint with the `discordServerId`
- The API will automatically add the guild to `activeGuilds` if under the limit
- If the guild is already active, verification succeeds without changes
- If the maximum guild limit is reached, verification fails

**Authentication:** Required (X-API-Key or Authorization Bearer)

**Request:**
```json
{
  "licenseKey": "LM-2F7A9C-D3E8F1-H5K2L0",
  "discordServerId": "123456789012345678"
}
```

**Note:** `discordServerId` is **required** for all verification requests. An optional `deviceId` field can be included for additional tracking if needed.

**Response (Valid):**
```json
{
  "valid": true,
  "message": "License is valid",
  "license": {
    "licenseKey": "LM-2F7A9C-D3E8F1-H5K2L0",
    "product": {
      "name": "Crim Tickets",
      "id": "prod_001"
    },
    "owner": {
      "userId": "usr_12345",
      "email": "client@example.com"
    },
    "status": "active",
    "validity": {
      "issuedAt": "2025-11-10T00:00:00.000Z",
      "expiresAt": "never",
      "durationDays": "lifetime"
    },
    "activation": {
      "maxGuilds": 1,
      "activeGuilds": ["123456789012345678"]
    }
  }
}
```

**Response (Invalid - Expired):**
```json
{
  "valid": false,
  "message": "License has expired",
  "expiresAt": "2024-12-31T23:59:59.000Z"
}
```

**Response (Invalid - Max Guilds Reached):**
```json
{
  "valid": false,
  "message": "Maximum number of guilds reached for this license",
  "maxGuilds": 1,
  "activeGuilds": 1
}
```

### License Management

#### POST `/api/licenses`

Create a new license.

**Authentication:** Required

**Request:**
```json
{
  "licenseKey": "LM-2F7A9C-D3E8F1-H5K2L0",
  "product": {
    "name": "Crim Tickets",
    "id": "prod_001"
  },
  "owner": {
    "userId": "usr_12345",
    "email": "client@example.com"
  },
  "validity": {
    "issuedAt": "2025-11-10T00:00:00Z",
    "expiresAt": "never",
    "durationDays": "lifetime"
  },
  "status": "active",
  "activation": {
    "maxGuilds": 1,
    "activeGuilds": []
  },
  "metadata": {
    "notes": "Client: Shrreyan, Crim Tickets",
    "reseller": "BuiltByBit"
  },
  "verification": {
    "lastVerified": "2025-11-10T18:31:00Z",
    "checksum": "c42aebf0e5195c8b930a1b1e1ab9d7f8"
  }
}
```

**Response:**
```json
{
  "message": "License created successfully",
  "license": { ... }
}
```

#### GET `/api/licenses`

Get all licenses.

**Authentication:** Required

**Response:**
```json
{
  "licenses": [...],
  "count": 10
}
```

#### GET `/api/licenses/:licenseKey`

Get a specific license.

**Authentication:** Required

**Response:**
```json
{
  "license": { ... }
}
```

#### PUT `/api/licenses/:licenseKey`

Update a license. You can update any nested fields.

**Authentication:** Required

**Request (Update Status):**
```json
{
  "status": "inactive"
}
```

**Request (Update Activation):**
```json
{
  "activation": {
    "maxGuilds": 5
  }
}
```

**Request (Update Product Info):**
```json
{
  "product": {
    "name": "Updated Product Name"
  }
}
```

**Response:**
```json
{
  "message": "License updated successfully",
  "license": { ... }
}
```

#### POST `/api/licenses/:licenseKey/deactivate`

Remove a Discord server from the license's active guilds. **Call this when your bot leaves a server or shuts down.**

**Authentication:** Required

**Request:**
```json
{
  "discordServerId": "123456789012345678"
}
```

**Response:**
```json
{
  "message": "Guild deactivated successfully",
  "license": { ... }
}
```

**Error (Guild Not Active):**
```json
{
